import React, { useState, useEffect } from 'react';
import Event from './Event.js';
import './Schedule.css';

export default function Schedule() {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState('2/24/2023');
  const [isLoading, setIsLoading] = useState(true); // Add loading state

  useEffect(() => {
    let url = 'https://adonix.hackillinois.org/event/';
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setEvents(data.events);
        setIsLoading(false); // Set loading to false when data is fetched
      })
      .catch((error) => {
        console.log('Error fetching event data:', error);
        setIsLoading(false); // Set loading to false on error
      });
  }, []);

  const formatDate = (timestamp) => {
    const date = new Date(timestamp * 1000); // Convert to milliseconds
    return date.toLocaleDateString('en-US'); // Format as MM/DD/YYYY (adjust the locale as needed)
  };

  // Generate a grid of dates for February 2023
  const generateCalendar = () => {
    const daysInMonth = new Date(2023, 1, 0).getDate(); // February 2023
    const monthName = new Date(2023, 1, 1).toLocaleDateString('en-US', { month: 'long' }); // Get the full month name
    const calendar = [];

    // Display the month name
    calendar.push(
      <div key="month" className="calendar-month">
        {monthName}
      </div>
    );

    // Generate the calendar grid
    for (let day = 1; day <= daysInMonth; day++) {
      const dateWithoutEvents = !events.some((event) => {
        const eventDate = formatDate(event.startTime);
        return eventDate === `2/${day}/2023`;
      });

      calendar.push(
        <div
          key={day}
          className={`calendar-day ${selectedDate === `2/${day}/2023` ? 'selected' : ''} ${
            dateWithoutEvents ? 'no-events' : ''
          }`}
          onClick={() => handleDateClick(day)}
        >
          {day}
        </div>
      );
    }
    return calendar;
  };

  const handleDateClick = (day) => {
    const selectedDateString = `2/${day}/2023`;
    setSelectedDate(selectedDateString);
  };

  const filteredEvents = events
    .filter((event) => {
      const eventDate = formatDate(event.startTime);
      return eventDate === selectedDate;
    })
    .sort((a, b) => a.startTime - b.startTime); // Sort events by start time

  return (
    <div className="schedule-container">
      <div className="calendar">
        {isLoading ? (
          <div className="loading">Loading...</div> // Render loading indicator while fetching data
        ) : (
          generateCalendar()
        )}
      </div>
      <div className="schedule">
        <ul>
          {isLoading ? (
            <div className="loading">Loading...</div> // Render loading indicator while fetching data
          ) : (
            filteredEvents.map((event) => (
              <Event
                key={event.id}
                id={event.id}
                name={event.name}
                description={event.description}
                startTime={event.startTime}
                endTime={event.endTime}
                locations={event.locations}
                points={event.points}
                eventType={event.eventType}
              />
            ))
          )}
        </ul>
      </div>
    </div>
  );
}
