import React, { useState } from 'react';
import Event from "./Event.js";
import "./Schedule.css"

export default function Schedule() {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);

  React.useEffect(() => {
    let url = "https://adonix.hackillinois.org/event/";
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setEvents(data.events);
      })
      .catch((error) => {
        console.log("Error fetching event data:", error);
      });
  }, []);

  const formatDate = (timestamp) => {
    const date = new Date(timestamp * 1000); // Convert to milliseconds
    return date.toLocaleDateString('en-US'); // Format as MM/DD/YYYY (adjust the locale as needed)
  };

  // Generate a grid of dates for February 2023
  const generateCalendar = () => {
    const daysInMonth = new Date(2023, 1, 0).getDate(); // February 2023
    const calendar = [];
    for (let day = 1; day <= daysInMonth; day++) {
      calendar.push(
        <div
          key={day}
          className={`calendar-day ${selectedDate === day ? 'selected' : ''}`}
          onClick={() => handleDateClick(day)}
        >
          {day}
        </div>
      );
    }
    return calendar;
  };

  const handleDateClick = (day) => {
    const selectedDateString = `02/${day}/2023`; // Assuming MM/DD/YYYY format
    setSelectedDate(selectedDateString);
  };

  return (
    <div className="">
      <div className="calendar">
        {generateCalendar()}
      </div>
      <div className="schedule">
        <ul>
          {events.map((event) => (
            <Event
              key={event.id}
              id={event.id}
              name={event.name}
              description={event.description}
              startTime={event.startTime}
              endTime={event.endTime}
              locations={event.locations}
              date={formatDate(event.startTime)}
            />
          ))}
        </ul>
      </div>
      <div className="selected-date">
        {selectedDate && <p>Selected Date: {selectedDate}</p>}
      </div>
    </div>
  );
}
