import React from 'react';
import "./Event.css";

export default function Event({ id, name, description, startTime, endTime, locations, points, eventType }) {
  // Function to format a timestamp to 12-hour clock format with AM/PM
  const formatTime = (timestamp) => {
    const date = new Date(timestamp * 1000); // Convert to milliseconds
    const hours = date.getHours();
    const minutes = date.getMinutes();
    const amOrPm = hours >= 12 ? 'PM' : 'AM';
    const hours12 = hours % 12 || 12; // Handle midnight (0) as 12 AM
    return `${hours12}:${minutes.toString().padStart(2, '0')} ${amOrPm}`;
  };

  // Calculate the event duration in minutes
  const eventDuration = (endTime - startTime) / 60;

  // Set points to 0 if it doesn't exist
  points = points || 0;

  // Define a custom class based on event duration
  let eventClass = "event";

  if (eventDuration <= 30) {
    eventClass += " short-event";
  } else if (eventDuration <= 60) {
    eventClass += " medium-event";
  } else {
    eventClass += " long-event";
  }

  return (
    <div className={`event-container ${eventClass}`}>
      <div className="event">
        <div className="event-header">
          <div className="first-row">
            <h3>{formatTime(startTime)}</h3>
            <h2>{name}{eventType !== "OTHER" ?  " - " + eventType : ""}</h2>
          </div>
          <div className="event-points">
            <p>Points: {points}</p>
          </div>
          <p className="event-time">
            {formatTime(startTime)} - {formatTime(endTime)}
          </p>
        </div>
        <p className="event-description">{description}</p>
        {/* Map through locations and display descriptions */}
        {locations &&
          locations.map((location, index) => (
            <p key={index} className="event-location">
              Location {index + 1}: {location.description}
            </p>
          ))}
      </div>
    </div>
  );
}
