import React from 'react';
import "./Event.css";

export default function Event({ id, name, description, startTime, endTime, locations }) {
  // Function to format a timestamp to 12-hour clock format with AM/PM
  const formatTime = (timestamp) => {
    const date = new Date(timestamp * 1000); // Convert to milliseconds
    const hours = date.getHours();
    const minutes = date.getMinutes();
    const amOrPm = hours >= 12 ? 'PM' : 'AM';
    const hours12 = hours % 12 || 12; // Handle midnight (0) as 12 AM
    return `${hours12}:${minutes.toString().padStart(2, '0')} ${amOrPm}`;
  };

  return (
    <div className="schedule-container">
      <div className="schedule">
        <ul>
          {filteredEvents.map((event) => (
            <Event
              key={event.id}
              id={event.id}
              name={event.name}
              description={event.description}
              startTime={event.startTime}
              endTime={event.endTime}
              locations={event.locations}
            />
          ))}
        </ul>
      </div>
      <div className="calendar">
        {generateCalendar()}
      </div>
      <div className="selected-date">
        {selectedDate && <p>Selected Date: {selectedDate}</p>}
      </div>
    </div>
  );

}
